css:
@-moz-document url-prefix("https://www.twitch.tv/") {
.channel-link .profile-photo {
    background: darkred;
    padding: 4px;
}
.conversations-content .conversations-list-icon {
background: darkred;
}

.button.primary:not(.disabled):hover, .follow-button:not(.ember-follow) .follow:not(.disabled):hover {
    background: darkred;
}

.button.primary, .follow-button:not(.ember-follow) .follow {
    background: darkred;
    color: #fff;
    padding: 0 10px;
    font-size: 12px;
}
}
