@-moz-document domain('superanimes.com') {
  :root {
    --dsp: none !important;
    
  }
  body, html {
    background: rgba(255,255,255,0.0) !important;
    font-family: tahoma !important;
  }
  img {
    border-radius: 2px !important;
  }
  #menuH {
    border-bottom: none !important;
  }
  #boxLogo, #boxLogo img {
    float: none !important;
    margin: auto !important;
  }
  .menu {
    display: var(--dsp) !important;
  }
  .menuMobile > span {
    display: var(--dsp) !important;  
  }
  #topo {
    width: 100% !important;
    text-align: center !important;
    margin: auto !important;
    border-radius: 3px !important;
  }
  #search {
    border: 2px solid rgba(247,247,247,0.99) !important;
    box-shadow:0px 0px 12px rgba(247,247,247,0.99) !important;
    background: rgba(247, 247, 247, 0) none repeat scroll 0% 0% !important;
  }
  .conteudoBoxHome {
    border: 2px solid rgba(247,247,247,0.99) !important;
    box-shadow:0px 0px 12px rgba(0,0,0,0.2) !important;
    text-align: center !important;
  }
  .boxDestakTImg, .boxDestakCorpoImg {
    background: #F2F2F2 !important;
    border: 1px solid #FCFCFC !important;
    border-radius: 3px !important;
    box-shadow:0px 0px 8px rgba(128,128,128,0.21) !important;
  }
  .conteudoBox {
    background: rgb(255,255,255) !important;
    border: 1px solid #FCFCFC !important;
    box-shadow:0px 0px 12px rgba(0,0,0,0.2) !important;
  }
  .feedBox, .boxBarraInfo {
    background: #F2F2F2 !important;
    border-radius: 3px !important;
  }
  .bannerHDesc{
    background: #F2F2F2 !important;
    border-radius: 3px !important;
    color: rgb(255, 102, 85) !important;
  }
  .star {
    color: rgb(255, 102, 85) !important;
  }
  .paginacao a, #comentario {
    background: rgb(255, 102, 85) !important;
    border: 1px solid rgba(255, 102, 85, 0.6) !important;
  }
  .maisConteudo {
    background: #EDEDED !important;
    border-top: 1px solid #F2F2F2 !important;
  }
  .tituloBox, .temporada, .boxSocial, .conteudoBox2 {
    background: #EDEDED !important;
    border: none !important;
  }
  .selectVideo {
    background: #FFF !important;
  }
  .btVideo {
    box-shadow: 0px 1px 1px !important;
  }
  .fb-comments, .fb-comments span, .fb-comments iframe {
    background: rgb(75,75,75) !important;
    border-radius: 3px !important;
  }
  .EpsMenu, .MenuEpsTitulo {
    background: rgb(255, 102, 85) !important;
    color: #FFF !important;
  }
  .menuEpsList{
    background: #FFF !important;
    border-bottom: 1px solid #EDEDED !important;
  }
  #corpoVideo {
    background: #F2F2F2 !important;
  }
  .boxVideoDes {
    background: #FFF !important;
    border: 1px solid #E6E6E6 !important;
  }
  #rodapeConteudo {
    display: var(--dsp) !important;
  }
  #rodape {
    box-shadow: 0px 0px 12px #ccc !important;
    border-top: none !important;
  }
  .sinop button {
    background: var(--dsp) !important;
  }
  .boxAnimeSobreLinha, .epsBox {
    border-bottom: 1px solid RGBA(51, 51, 51, 0.14) !important;
  }
  #eps > span > img, #animeLancamento > span > img, #lastAnimes > span > img, #tokusatsu > span > img, #liveAction > span > img, #filmes > span > img {
    display: var(--dsp) !important;
  }
}
