css:
@-moz-document domain("cpp-netlib.org") {
.navbar-inverse .navbar-brand {
	color: #f1c40f;
}

.navbar-inverse .navbar-nav>li>a {
	color: #ecf0f1;
}

.cpp-netlib-navbar {
	background-image: linear-gradient(to bottom,#34495e 0,#2c3e50 100%);
	border-color: rgba(0, 0, 0, 0);
}

.navbar-inverse .navbar-nav>.active>a {
	background: rgba(0, 0, 0, 0.5);
	box-shadow: none;
	color: #e67e22;
	text-shadow: none;
	cursor: default;
}

.navbar-inverse .navbar-nav>li>a:hover, .navbar-inverse .navbar-nav>li>a:focus {
	background-color: rgba(0, 0, 0, 0.2)!important;
	text-shadow: none;
}

.navbar-inverse .navbar-nav>.active>a:hover {
	color: #e67e22;
	background: rgba(0, 0, 0, 0.5)!important;
}

.navbar-inverse .navbar-brand:hover, .navbar-inverse .navbar-brand:focus {
	color: #f1c40f;
	cursor: default;
}

/* scroll menu */

.cpp-netlib-sidebar .nav>li>a:hover, .nav>li>a:focus {
    background-color: transparent;
    color: #eee;
}

.cpp-netlib-sidebar .nav>li:not(.active)>a:hover, .nav>li:not(.active)>a:focus {
    color: #333333;
}

.cpp-netlib-sidebar li.list-group-item:hover {
    background-color: #eee;
}

#footer {
    padding: 2em 1em;
    height: auto;
}
}
