css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lumendatabase.org") {
  body {
    background: #444;
    color: darkorange;
  }
  
  body.home {
    background: #444;
  }
  
  header.app, section.main.app, footer.main {
    background: #444;
  }
  
  article.notice-show div.main {
    background: #666;
  }
  
  article.notice-show div.other-entities, header.app .container.header-bar, article.notice-show h1, article.notice-show header, article, aside, details, figcaption, figure, footer, header, hgroup, main, nav, section, summary, aside.notice, article.notice-show footer, footer.main div.inner-wrapper, footer.main, .site-news {
    background: #888;
  }
  
  footer.main span, footer.main a {
    color: #ccc;
  }
  
  textarea, input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="url"], input[type="color"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="time"], input[type="week"] {
    background: #bbb;
    color: #444;
  }
  
  header.app .container div.input::before, section.home-search-form div.input::before {
    color: #444;
  }
  
  header.app ol.nav-list li.nav-item > a {
    color: #59f;
  }
  
  a:link, a:visited {
    color: #7af;
  }
  
  article.notice-show ol.copyright-claims li.work span.label {
    color: #ccc;
  }
  
  aside.notice section:first-child {
    box-shadow: 0px 0 0 #fff;
  }
}
