css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com") {

/*ads*/
#ads_left {
    display: none!important;
}
/*shadows*/

.page_block {
    box-shadow: 0 1px 10px 0 #DDD!important;
}
 
    
/* unread messages background color */
.im-page--history ._im_mess_unread,
.im-page--history ._im_mess_unread.im-mess_selected,
.im-page--history ._im_mess_unread.im-mess_selected:hover
{
    background-color: #dddfee;
}
 
.im-page--dialogs .nim-dialog_unread-out:not(.nim-dialog_selected),
.im-page--dialogs .nim-dialog_unread-out:hover:not(.nim-dialog_selected),
.im-page--dialogs .nim-dialog_unread:not(.nim-dialog_selected),
.im-page--dialogs .nim-dialog_unread:hover:not(.nim-dialog_selected)
{
    background-color: #dddfee;
}
  
/*chats list on the right*/

body 
{
    letter-spacing: 0;
}
.im-page .im-page--dialogs
{
    float: right;
}
.im-page .im-page--history
{
    margin-left: 0;
    margin-right: 317px;
}
    
/*more square-ish avatars*/
div.friends_added_help_rows.clear_fix img,
.module_body .people_cell_img,
.page_list_module .thumb,
.wall_module .copy_post_img,
a.fc_msgs_img,
.nim-peer .nim-peer--photo-w,
.im-member-item .im-member-item--photo,
.post_img,
.wall_module .reply_img,
.chat_tab_img,
img.fc_contact_photo,
.top_notify_cont .feedback_img,.feedback_group_photo_img,.feedback_img,
.fave_link_thumb,
.fans_fan_img,
.right_list_img,.friends_photo_img,.ow_ava.ow_ava_small,
.group_row_photo, .group_row_img,
.page_list_module .cell_img,
.group_box_image_wrap, .group_box_image_link,
.bp_img,.top_profile_img,.ba_thumb_wrap,.ow_ava.ow_ava_comm,.like_share_ava.wdd_imgs,.wddi_img,img.settings_bl_photo,.like_tt_image, .poll_tt_image 
{
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -o-border-radius: 3px !important;
    border-radius: 3px !important;
}
    
}
