css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("securedns.comodo.com") {
  body {
    background: #444;
  }
  
  .bf_results h2, .comodo_form {
    background: #666;
  }
  
  .bf_desc h1, .bf_results h2, .bf_results p, .bf_related h1, .comodo_footer .copyright, .comodo_footer .links, .comodo_footer .links a {
    color: #aaa;
  }
  
  .comodo_footer .links a:hover {
    color: gold;
  }
  
  .bf_related li a {
    color: goldenrod;
  }
  
  .bf_related a:hover, .bf_results a:hover {
    color: gold;
  }
  
  .comodo_header img {
    filter: invert(0.735);
  }
  
  .comodo_form input {
    background: #888;
  }
}
