css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("google.co.uk") {
  /* (c) 2008 by Lestarte */

  object[data*="content"],

  ol {
    counter-reset: pageNum;
  }

  li h3:before {
    content: counter(pageNum) ": ";
    counter-increment: pageNum;
    font-weight: bold;
  }
}
