css:
@-moz-document url("http://www.aywas.com/avt/") {
#dialogue-text {
    float: left;
    width: 50%;
    margin: 5px 0;
    padding: 0;
}
#dialogue-actions {
    float: right;
    width: 50%;
    margin: 0;
    padding: 0;
    text-align: right;
}
#avt-dialogue {
    min-height: 100px;
    padding: 10px;
    width: 80%;
    margin-left: auto;
    margin-right: auto;
    border-bottom: 0px;
    border-left: 0px;
    border-right: 0px;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
}
label.ctabtn.large {
    max-width: 45%;
}
input.adv-action,
a.ctabtn.large.modal-link span {
    max-width: 100%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
a.ctabtn.large.modal-link span {
    position: absolute;
    width: 50%;
    margin: 0;
    padding: 0;
    left: 50%;
    margin-left: -25%;
}
}
