css:
/* i really want this to be global */



/*Spritesheet*/

.arrow-downvote,
.arrow-downvoted,
.arrow-upvote,
.arrow-upvoted,
.comment .arrow-downvote,
.comment .arrow-downvoted,
.comment .arrow-upvote,
.comment .arrow-upvoted,
#manage,
#mail.nohavemail,
#mail.havemail {
    background-image: url(http://i.imgur.com/PnOxT0k.png);
}



/*Misc Styling*/

.score.likes {
    color: #FF8B60 !important
}
.score.dislikes {
    color: #9494FF !important
}
.scorebar-likes {
    background: #FF8B60
}
.scorebar-dislikes {
    background: #9494FF
}
.rss-icon {
    display: none
}
a:visited {
    color: #551a8b
}

/*Comments*/

.author.submitter {
    background: blue;
    color: white !important
}
a.submitter {
    color: blue
}
.tagline .post_upvotes {
    color: #FF8B60
}
.titlebox h1 a:hover {
    color: black;
    text-decoration: underline
}
.titlebox .bottom {
    border-top: 1px solid black
}
.alert-h1 {
    color: gray
}
.sidecontentbox .content {
    border: 1px solid gray
}
/*Sign-in*/

#loginForm .form-horizontal {
    background: white;
    border: 0px
}
.modal-title {
    color: #4270a2
}
.modal-header {
    background: white;
    border: 0px
}
#loginForm .form-control {
    width: 340px;
    border-top: 1px grey
}
.modal-body .btn-whoaverse {
    background: #4f86b5;
    border-bottom: 2px solid #4270a2;
    color: #ffffff;
}
