css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://m.mlb.com/scoreboard) {
  #top-ad { display: none; }
  div.ad-skin { display: none; }
  DIV.menu-container { display: none; }
  SECTION.section-scoreboard2 header { display: none; }
  DIV.layout-scoreboard2 aside.span-3.right { display: none; }
  DIV#sbTicker[data-active="true"] { display: none; }
  .game .game-review { display: none; }
  .game .game-lookin { display: none; }
  #global-footer-container { display: none; }
  SECTION.scoreboard-footer { display: none; }
  object[type="application/x-shockwave-flash"] { display: none; }
  
  #scoreboard_index .column.primary, #scoreboard2_index .column.primary { width: 100%; }
  li.game { margin-left: 6px !important; }
  .game.alert, .game.review, .game.managerchallenge { border: 1px solid #ccc !important; }
  #scoreboard_index .container, #scoreboard2_index .container, #home_index .container { max-width: none; }

  div.drawer_container { display: none; }
}
