css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.geekculture.dk") {
body { background: #E0E0E0 !important; color: #000000 !important; }
body,td { font-family: Verdana; font-size: 12px; line-height: 16px; }
a:link { color: #0040FF; }
a:visited { color: #5070FF; }
a:hover { color: #40C0FF; }
a:active { color: #FFFFFF; }
.default-box { color: #000000; }
td { padding: 1px 4px 1px 4px; }
.sidebar-box { background: #003000 !important; }
sidebar-box-headline { background: #003000 !important; }
.sidebar-box td { background: #C0C0C0 !important; }
.fact-box td { color: #000000; }
input,textarea { background: #C0C0C0; color: #202020; border: solid 1px; font-family: verdana; font-size: 12px; }
}
