css:
@-moz-document domain("mobile.twitter.com") {
body {
  max-width: 40rem;
  margin: 0 auto;
}
.tweet-box-textarea {
  resize: vertical;
}
.tweet-box-textarea:focus {
  min-height: 30vh;
}
.profile-similar-to-container {
  display: none;
}
.profile-content {
  display: flex;
  flex-direction: column;
}
.module.stats-lists {
  order: -1;
}
.module.stats-lists ul {
  display: flex;
  text-align: center;
}
.profile-menu-item {
  flex: 1;
}
.profile-menu-item:hover {
  background-color: #ffe;
}
}
