css:
@-moz-document domain("alternativeto.net") {
.img-thumbnail,
body {
	background-color: #111111;
}
.alternatives-selector {
	border-bottom: 1px solid #212121;
}
.border-left {
	border-left: 1px solid #212121;
}
h1.underline,
h2.underline,
h3.underline,
.app-list li,
ul.members li:not(:last-child) {
	border-bottom: 1px solid #212121;
}
.label-platform {
	color: #ccc;
	background-color: #232323;
}
.label-feature,
p a.label,
p span.label {
	background-color: #333;
}
.label[href]:focus,
.label[href]:hover {
	background-color: #111111;
}
.label-feature a,
a.label-feature {
	color: #dadada !important;
}
.label-license,
.label-platform {
	border: 1px solid #212121;
}
.label-license {
	background-color: #5090aa;
	color: #fff;
}
.label-discontinued,
.label-discontinued[href]:focus,
.label-discontinued[href]:hover {
	background-color: #969696!important;
}
section.blue {
	background: #0F2435;
}
body > header {
	background: #193044;
	color: #fff;
}
.alternatives-selector .headings h1 span.license-filter,
.alternatives-selector .headings h1 span.platform-filter {
	color: #757575;
}
.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
h1,
h2,
h3,
h4,
h5,
h6 {
	color: #3C6384;
}
.btn-default {
	color: #3d3d3d;
	background-color: #111111;
	border-color: #212121;
}
.btn-default.active,
.btn-default.focus,
.btn-default:active,
.btn-default:focus,
.btn-default:hover,
.open > .dropdown-toggle.btn-default {
	color: #555555;
	background-color: #111111;
	border-color: #212121;
}
h2 a {
	color: #3c84b4;
}
.lightbox__nav--prev:before {
	content: '\e838';
}
.lightbox__nav--next:before {
	content: '\e839';
}
.opinion-comments .opinion-note {
	background: #333;
	border: 1px solid #333;
	color: #ccc;
}
section.widget .news-item h3 a {
	color: #c4c4c4;
}
section.widget .news-item p > a {
	color: #919191;
}
.headline-link {
	background: #222;
	color: #118ddb;
	margin-left: 10px;
}
.customList .h1,
.customList .h3,
.customList h1,
.customList h3 {
	color: #ccc;
}
.customList #listDescription,
.customList #listSummary,
.customList .itemDesc textarea,
.customList li,
.customList p,
.customList .article-meta .user-meta {
	color: #f5f5f5;
}
body > header:not(:hover) .simple-main-new {
	background: #102d40;
	box-shadow: inset 0 -1px #3c84b4;
}
.customList {
	background-color: #212121;
}
.report-opinion {
	background-color: transparent;
}
.container.alternatives {
	background-color: #212121;
	color: #f5f5f5;
}
.content-box {
	color: #ccc !important;
	background-color: #333;
}
.content-box .discrete-header {
	color: #7091a7;
}
.activity-list li {
	background-color: #333;
	border-color: transparent;
}
hr {
	border-color: #444;
}
}

@-moz-document domain("alternativeto.net") {
/*	quickfix for "Free Open Source" label	*/
.label-pricing .oss-label {
	font-family: ProximaNova-Semibold, "Helvetica Neue", Helvetica, Arial, sans-serif;
	font-size: 14px;
	top: 0px;
	margin-left: 0;
	color: #37a282;
}
#appHeader > div > div > ul > li:nth-child(1) > span > span.oss-label {
	color: #a9decd !important;
}

/*	bundelware lable fix	*/
.label-bundleware {
	color: #333 !important;
}

/*	know any alteratives	*/
.panel-info > .panel-heading,
.panel-info {
	color: #31708f;
	background-color: #0b2532;
	border-color: #333;
}
.panel-info .panel-body {
	background: #051015;
	color: #31708f;
}

/*  quick pnael fix  */
.app-sidebar {
	margin-top: 0 !important;
}

/* news colorfix */
.single-news-item p + p,
body {
	color: #c1c1c1 !important;
}

/* load more apps */
.btn.btn-load-more {
	color: #3d3d3d;
	background-color: #111111;
	border-color: #212121;
}
.btn.btn-load-more:hover {
	color: #565656;
	border-color: #212121;
	background-color: #333333;
}

/* Alternetives color */
section.blue .nav-tabs > li.active > .tab-link {
	background-color: #0b1b28;
	color: #ffffff;
}
section.blue .nav-tabs > li.active > .tab-link:hover {
	background-color: #1c4363;
}
}
