css:
@-moz-document url-prefix("http://developer.android.com/guide/"), url-prefix("https://developer.android.com/guide/") {
html, body {
    background: #263238;
    color: #ECEFF1;
}
h1, h2, h3 {
    color: #CFD8DC;
}
.landing-docs h3 {
    color: #CFD8DC !important;
}
code {
    background: #37474F;
    color: #8BC34A;
}
#nav li.expanded .nav-section-header {
    background: #37474F;
}
#nav li.selected ul li a {
    color: #ECEFF1;
}
#nav a {
    color: #ECEFF1;
}
#nav li.expanded ul > li {
    background: #455A64;
}
#nav li.expanded > .nav-section-header:after, #nav .nav-section-header:after {
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
.landing-docs a {
    color: #ECEFF1 !important;
}
.jspVerticalBar {
    opacity: .25;
}
pre.prettyprint {
	background: #1f2223;
    border: none;
    box-shadow: rgba(0, 0, 0, 0.5) 0px 2px 4px;
    font-family: 'DejaVu Sans Mono';
    font-size: 10pt;
}
.kwd {
    color: #a57b61;
    font-weight: bold;
}
.pln, .pun, .tag {
	color: #E0E2E4;
}
.typ, .atn {
	color: #678CB1;
}
.lit {
	color: #A082BD;
}
.com {
    color: #7D8C93;
}
.atv, .str {
	color: #c78d9b;
}
.dac-footer {
	background: transparent;
}
.dac-footer .dac-footer-getnews, .dac-footer .dac-footer-contact-link {
    color: #ECEFF1;
}
}
