css:
@-moz-document domain("google.fr"), domain("google.com") {
div#searchform {
 	position: fixed !important;
    /*/top: -1em !important;/*/
    opacity: 0.8;
    transition: all .8s ease;
    box-shadow: #000 0 0 0;
    border-bottom: #223961 1px solid !important;
}


#searchform * {	font-size:1rem !important; transition: all .8s ease;}


	
#searchform:hover {
    opacity: 1;
    box-shadow: #000 0 1em 2em;
    /*/top: 1em !important;/*/
    border-bottom: #4285F4 4px solid !important;
    box-shadow: #7E42F4 0 -1em 2.3em 1em,#000 0 2em 4em;
}
	
#searchform:hover * {	font-size:1em !important;	}

.sbibtd {
    width: 20vw;
	transition: all 2.4s ease !important;
}
.sbibtd:hover {
    width: 80vw;
	transition: all 1.4s ease !important;
}

div#top_nav {
    position: fixed;
    top: 4.3em;
    width: 100%;
    z-index: 100;
    opacity: .4;
    transition: all .8s ease;
}

div#top_nav:hover {
    opacity: 1;
}

div#rcnt {
    margin-top: 3em;
}

div#rshdr {
    height: 7em;
}

div#sfcnt {
    top: 0;
}

div#cnt {padding: 0;}

div#qbc {
    top: 0;
    position: fixed;
}

.gb_ib.gb_Cf.gb_R.gb_Bf.gb_T {
    top: 1em;
    position: fixed;
    right: 0;
}


.r-iDo8daJt8MIk {
    top: 0;
}
}
