css:
@-moz-document url-prefix("http://dota2.ru/") {
body .full {
	background: #121215 !important;
	padding: 0 !important;
}
  
body .full > div > a {
    top: 0 !important;
	left: 0 !important;
	width: 0 !important;
	height: 0 !important;
    visibility: hidden !important;
    padding: 0 !important;
    margin: 0 !important;
}
  
.wrap,
.full-wrap {
	padding-top: 110px !important;
    box-shadow: none !important;
}
  
nav,
.menu {
    width: 100%;
}
  
.a-top-left { top: 137px; }
.a-right-top { top: 562px; }
.a-left-top { top: 1709px; }
.a-top-right { top: 167px; }
.a-left-top.sidebar { top: 1612px; }
.hadv-block, .hadv-block2 { top: 137px; }
.hv-right { top: 167px; }
	
#click12 {
	position: absolute;
	top: 0 !important;
	left: 0 !important;
	width: 0 !important;
	height: 0 !important;
}

.m-banner240, .bn240 {
    top: 0 !important;
	left: 0 !important;
	width: 0 !important;
	height: 0 !important;
    visibility: hidden !important;
    padding: 0 !important;
    margin: 0 !important;
}

.m-forum {
    width: 595px !important;
}

.m-forum .text {
    width: 500px !important;
}

.m-battle-blogs, .m-blogs {
    width: 595px !important;
}

header {
    height: 100px;
    margin-left: 0 !important;
    margin-right: 0 !important;
    width: 100% !important;
    position: fixed !important;
    top: 0 !important;
    left: 0 !important;
    z-index: 500 !important;
}

body header .subline { margin: 0 }
body header .banners {
    top: 0 !important;
	left: 0 !important;
	width: 0 !important;
	height: 0 !important;
    visibility: hidden !important;
    padding: 0 !important;
    margin: 0 !important;
}

.WidgetFramework_WidgetRenderer_Callback,
.WidgetFramework_WidgetRenderer_OnlineStaff {
    top: 0 !important;
	left: 0 !important;
	width: 0 !important;
	height: 0 !important;
    visibility: hidden !important;
    padding: 0 !important;
    margin: 0 !important;
}

#headerMover #headerProxy { height: 0; }

}
