css:
@-moz-document domain("wikirhymer.com") {
a.navbar.brand {
    display: none;
}
.navbar .nav>li>a {
    padding: 0;
}
#search-div {
    text-align: left;
    margin: 0;
}
#search-div #search-field {
    margin-right: 0;
    width: 145px;
}
.card {
    margin: 0;
    padding: 0;
}
.adsense {
    display: none;
}
.description {
    padding: 0;
}

.card~.description {
    margin-top: 8px;
}
.container {
    width: 100%;
}
.dropdown {
  margin-right: 0.5em;
}
.card .footer {
  font-size: 8px;
  line-height: 10px;
  margin: 0;
  opacity: 0.4;
}
.words, .phrases {
  margin: 0;
}
footer {
  display: none;
}
.navbar .brand {
  display: none;
}
.card~.description {
  margin-top: 0;
}
.accordion-inner {
  display: none;
}
.button {
  padding: 0.2em;
}
.card.limit {
  font-size: 50%;
}
.card.limit .button {
    font-size: 8px;
}
p {
  line-height: 1;
}
.accordion-heading .accordion-toggle {
  display: block;
  padding: 0px 0;
}
h3, a.accordion-toggle {
  font-size: 7px;
  line-height: 9px;
  opacity: 0.4;
}
.description {
  padding: 0em 0.5em !important;
}
html, body {
  font-size: 93%;
}
.navbar {
    display: none;
}
.limit p {
  opacity: 0.4;
}
.phrases li {
  display: inline-block;
  padding: 0px;
  padding-right: 9px;
  border-right: 1px solid white;
  margin-right: 9px;
}
}
