css:
@-moz-document domain("phoenix.corvidae.org") {
/* Maximum total signature height
Anything beyond this gets chopped off */
div[data-role="memberSignature"] {
    max-height: 120px !important;
    overflow: hidden;
}

/* Maximum image height */
div[data-role="memberSignature"] img {
    max-height: 100px !important;
}

/* Fix a specific font size and spacing */
div[data-role="memberSignature"] p,
div[data-role="memberSignature"] a, 
div[data-role="memberSignature"] span,
div[data-role="memberSignature"] div {
    font-size: 10px !important;
    line-height: 1.0 !important;
}
}
