css:
@-moz-document domain("codepen.io") {

body.layout-side .top-boxes {
  min-width: 0;
}
body.layout-top .top-boxes {
  height: 0;
  min-height: 0;
}
.main-header {
  min-width: 0;
}

.pen-settings-modal {
	min-width: 75vw;
}

@media (max-width: 720px) {
  html {
    font-size: 10px;
  }
  .primary-actions>a, .primary-actions>button, .primary-actions>.button, .primary-actions>.file-upload-button, .primary-actions>.multi-button, .view-switcher-area .button {
    font-size: 0;
  }
  #save-as-private::before {
    content: '(';
    font-size: 1rem;
  }
  #save-as-private::after {
    content: ')';
    font-size: 1rem;
  }
}

}
