css:
@-moz-document domain("yenrab.github.io") {
body {
    margin: 0px;
}
#barneyHeader {
    position: relative;
    display: block;
    width: 100%;
}
#tagLine {
    padding: 10px;
}
#className {
    background: #005;
    padding: 10px;
    font-size: .8em;
    height: 45px;
}
#barneyWrap {
    position: relative;
    max-width: 1000px;
    margin: auto;
    overflow: hidden;
    border: none;
}
#barneyBody {
    top: 0px;
    width: 100%;
}
#barneySidebar {
    position: relative;
    display: inline-block;
    top: 0px;
    width: 15%;
    max-width: 160px;
    margin: 0px;
    min-width: 70px;
    background: black;
}
#scrollContainer {
    display: inline-block;
    width: 75%;
    left: 0;
    vertical-align: top;
}
#barneySidebar > table > tbody > tr > td > figure {
    margin: 20%;
}
#barneySidebar > table > tbody > tr > td > figure> img {
    width: 60%;
    min-width: 40px;
}
#barneySidebar > table > tbody > tr > td > img {
    margin: 5%;
    width: 40%;
    min-width: 40px;
}
#barneySidebar > table > tbody > tr > td:hover {
    background-color: #006;
    transition: .3s;
}
#barneySidebar > table > tbody > tr > td {
    transition: .3s;
    background-color: #002;
}
#barneySidebar footer {
    padding: 10px;
}
#barneySidebar footer a {
    color: #998;
}
h1 {
    color: #006;
}
h2 {
    color: #006;
}
}
