css:
@-moz-document domain("fr.ubergizmo.com") {

	body {
	  background-color: #333 !important;
	  color: #fff !important;
	  min-height: 0 !important;
	}
	  
	.single #info-post, .taboola-below-article-thumbnails, .author-avatar, #entry-social-sharing, #help-question, #comments, .relatedStoriesBox, footer {
	  display: none !important;
	}
	  
	div, .pagination .page-numbers {
	  background-color: #333 !important;
	  color: #fff !important;
	  text-shadow: none !important;
	  min-height: initial !important;
	  border: none !important;
	}
	
	h1, h2, h2 a, h3, h4, h5 {
	  text-shadow: none !important;
	  color: #fff !important;
	}
}
