css:
@-moz-document url-prefix("http://sync-video.com/r/") {
/* Main Body */

.navbar-fixed-top .navbar-inner, .navbar-static-top .navbar-inner { background-color: black; color: white; }
#wrap { background-color: #111111; color: white; }
#footer { background: black; border-top: 0px; }
.row-fluid [class*="span"]:first-child { background: none; border: 0px; }
a { color: #00a8ff; }


/* Homepage */


/* Sidebar */

#playlist>li.active { background-color: #343434; }
.playlist-left>h5 { color: white; }
.row-fluid [class*="span"]:first-child { color: white; }
#config-table tr td:nth-child(2) { background: black; }
#config-table tr td:first-child { background: black; }
.add-to-playlist-form .input-append input[type="text"] { background: black; border: 1px black solid }
table { border: 0px; }
tbody { border: 0px; }

/* Chat */

.chat .dialog li:nth-child(odd) { background: #4f4f4f; }
.chat .dialog li:nth-child(even){ background: #454545; }
.chat .dialog{ background: #454545; }

/* Cookies */

#cookies { background: black; border: 0px; color: white; }
.alert .close { color: white; }
}

