@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("e-klase.lv") {
  body {
    background: rgb(34, 34, 34);
  }
  
  #ox_banners_on_right {
     visibility: hidden;
    width: 0px !important;
    height: 0px !important;
  }
  
  #TopmostHeader {
    visibility: hidden;
    width: 0px !important;
    height: 0px !important;
  }
  
  .gray_box {
    background-color: rgb(34, 34, 34);
    color: rgb(255, 255, 255);
  }
  
  #news_short_cont a {
    color: rgb(255, 255, 255);
  }
  .n-minsection-title {
        color: rgb(10, 141, 209);
  }
  
  #HeaderText {
    background-color: rgb(34, 34, 34);
  }
  
  .th_darker {
    background-color: #4078c0;
  }
  .table th {
    background-color: #4E4E4E
  }
  
  .inf_par_profilu, .inf_par_profilu_item {
    background-color: #389c0a
  }
  
  ul.jmenu li.odd, .blue_div {
    background-color: rgba(0, 0, 0, 0);
  }
  ul.jmenu li.header {
    background-image: url(http://i.imgur.com/SUOQazH.png);
  }
  
  #read_mail_cont {
   color: rgb(255, 255, 255); 
  }
  
  .odd_row {
    background: #4E4E4E;
  }
  #tbl_msg_list {
    color: rgb(255, 255, 255);
  }
  
  tr.day-name > th {
    background-color: #4E4E4E;
    color: rgb(10, 141, 209);
  }
  
  th, td.caption {
    color: #FFFFFF;
    background: rgb(10, 141, 209);
  }
  
  table.table3 tr:nth-child(2n+1) {
    background-color: #4E4E4E;
  }
  
  .row1 {
    background-color: rgb(0, 0, 0);
  }
  
  h1 {
    color: #FFFFFF;
    background: rgba(0, 0, 0, 0);
  }
  
  #tinymce {
    color: rgb(255, 255, 255);
  }
  
}
