css:
@-moz-document url("http://www.youtube.com/editor"), url("https://www.youtube.com/editor") {
.flex-width-enabled .content-alignment {
    max-width: 1920px
}
#creator-sidebar .creator-sidebar-branding h1 {
    display: none;
}
#creator-page-sidebar.yt-card {
    max-width: 50px;
}
#creator-sidebar .creator-sidebar-section h3 a span.header-text {
    display: none;
}
#creator-sidebar .creator-sidebar-bullet, #creator-sidebar .creator-sidebar-section a:hover .creator-sidebar-bullet {
    display: none;
}
#creator-sidebar .creator-sidebar-submenu {
    display: none;
}
.yt-uix-button-dark {
    height: 20px;
    width: 20px;
    font-size: 0px; /* LOLHACK */
}
#preview-player, #preview-blackbox {
    width: 800px;
    height: 510px;
    line-height: 510px;
}
#mediapicker {
    left: 800px;
    height: 510px;
}
.media-list {
    height: 410px;
}
#infobox-container {
    left: 800px;
    height: 510px;
}
}
