css:
@-moz-document url-prefix("https://dev.detendusdupad.com/") {
html,
body,
#header {
	background: #DFF2FD;
	background-image: url(https://detendusdupad-dev-discourse-uploads.s3-eu-west-1.amazonaws.com/original/1X/1e488f7c080b57c02eaa0ddde51a2a1294d9e59f.png);
}
#nav-topbar {
	-webkit-border-top-left-radius: 5px;
	-webkit-border-top-right-radius: 5px;
	-moz-border-radius-topleft: 5px;
	-moz-border-radius-topright: 5px;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}
#header {}
#header .site-title img {
	max-height: 150px;
}
#header > div.container {
	background: #41C0F0;
}
#nav-topbar.nav-container {
	box-shadow: none;
	background: white;
	max-width: 1380px;
	margin: 0 auto;
}
#header .pad {
	padding: 0;
}
#header .site-title {
	padding: 0;
}
#nav-topbar .nav li.current-menu-ancestor>a,
#nav-topbar .nav li.current-menu-item>a,
#nav-topbar .nav li.current-post-parent>a,
#nav-topbar .nav li.current_page_item>a,
#nav-topbar .nav li:hover>a,
#nav-topbar .nav li>a:hover,
#nav-topbar .nav li a {
	color: #004563;
	font-weight: bold;
	background: linear-gradient(to bottom, transparent 50%, #41C0F0 50%);
	background-size: 100% 200%;
	background-position: top;
	transition: all 0.1s linear;
}
#nav-topbar .nav li.current-menu-item>a:hover,
#nav-topbar .nav li.current_page_item>a:hover,
#nav-topbar .nav li a:hover {
	color: white;
	background-position: bottom;
}
@media only screen and (min-width: 1025px) {
	.full-width #nav-topbar.nav-container {
		position: static;
	}
}
}
