css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lucariostream.eu") {
  #wrapper, #footer-wrapper, .dmp_Player, .breadcrumbs, .post {
    background: #666 !important;
    color: #ccc !important;
  }
  
  .readmore a {
    background: #555 !important;
    color: #c55 !important;
  }
  
  span, span a {
    color: #ccc !important;
    background: #666 !important;
  }
  
  .header-inner {
    background: #888 !important;
    background-image: url("http://1.bp.blogspot.com/-DzndNgm0fls/VpuP0wrM4LI/AAAAAAAAGXk/QDR_ZuvsggU/s1600-r/luicario%2Bstream_20160117_131332.jpg") !important;
    background-position: left center !important;
    width: 1000px !important;
    min-height: 320px !important;
  }
  
  div {
    color: #FF7F00 !important;
    background-color: #666 !important;
  }
  
  #search-form input#search-box[type="text"] {
    background: #777 !important;
    color: #ddd;
  }
  
  #video-640x360, #HTML1, #HTML5, #HTML17, #HTML10, #HTML14, .separator, #comments {
    display: none;
    width: 0px;
    height: 0px;
  }

  #backtop {
    background-color: rgba(0, 0, 0, 0);
  }
}
