css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gigatribe.com") {
  body {
    color: #888;
    background: #333;
  }
  #header {
    filter: invert(0.8);
  }
  #gigatribeLogo img {
    filter: invert(0.75);
  }
  #mainmenu a:hover, #mainmenu a:active, .identitifation > a:hover {
    color: #1be !important;
  }
  #mainmenu a:link, #mainmenu a:visited, .identitifation > a {
    color: #5ff;
  }
  .identitifation > a {
    color: #5ff !important;
  }
  .mainmenu-tabs ul li.selected a, .drop-menu ul li.selected a {
    color: #1be !important;
  }
  .content, .gold, .topright, .topleft, .bottomright, .bottomleft, #topFooter, .silv .left, .silv .right {
    background: #444 !important;
  }
  .threeColDouble {
    margin: 15px 15px 2px 0;
  }
  #send {
    background: #222 !important;
    color: #ccc !important;
  }
  a:hover, #footer a:hover, #footer a:active {
    color: #E28866 !important;
  }
  a, a:visited, #footer a:link, #footer a:visited {
    color: #E24422;
    text-decoration: unset;
  }
  #smallPush h1 {
    color: #333;
  }
  #footer {
    background: #333;
  }
}
