css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("uptobox.com") {
  html, body, .bg_page, .form_style, .block_form, #output, #container_page {
    background: #444 !important;
    color: #999;
  }
  #login-form-username, #login-form-password {
    background: transparent url("data:image/png;base64,");
    border: 1px solid #bbb;
    color: #aef;
  }
  .black_title {
    color: #ccc;
  }
  .bg_page > div:nth-child(5) > form:nth-child(4) > center:nth-child(9) > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    display: none;
  }
  .bg_page > div:nth-child(5) > form:nth-child(4) > center:nth-child(9) > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) {
    display: none;
  }
  table td {
    border: 0px;
  }
  
  #maincore, #topbanner, .inputframe {
    background: #666 !important;
  }
  #input_text_file, #leftloginbox input.loginstyledlabel, #leftloginbox input.loginstyled, input.loginstyledlabel, .tableftgap, .tabbulk, #toptoolbar select, .form_style > input, .captcha_code {
    background: #888 !important;
    color: #aef;
    border: 1px solid #bbb;
  }
  .searchbutton {
    width: 75px;
    display: inherit;
  }
  .text_input, .searchqueryactive {
    color: #ddd !important;
  }
}
