css:
@-moz-document domain("tweetdeck.twitter.com") {
/* Fix "View Details" and action items floating left */
a[rel="viewDetails"] {
    display: none;
}
.tweet-footer {
    margin-top: 7px;
}

.tweet-actions {
    float: right;
    width: initial !important;
}
.tweet-actions > * {
    margin-left: 13px;
    margin-right: 0 !important;
}


}
