css:
@-moz-document domain("w3sh.com") {
  
  /* Popup */
  div.fancybox-inner, div.fancybox-overlay, .fancybox-wrap .fancybox-desktop .fancybox-type-inline .snp-wrap .snp-pop-56601-wrap .snp-pop-theme1-wrap .fancybox-opened, .fancybox-outer, .fancybox-close {
    height: 1px !important;
    width: 1px !important;
    position: fixed !important;
    display: none !important;
    left: 0 !important;
    top: 0 !important;
  }
  div.fancybox-overlay-fixed, .snp-wrap .fancybox-skin {
    background: none !important;
  }
  
  /* Main theme */
  body, .custom-background {
    background: none !important;
    background-color: #222 !important;
    color: #999 !important;
  }
  div.backstretch, header.cb-header {
    display: none !important;
  }
  .cb-menu-light #cb-nav-bar .cb-nav-bar-wrap {
    margin-top: 20px !important;
    background-color: #222 !important;
    color: #999 !important;
  }
  .cb-menu-light #cb-nav-bar .cb-main-nav > li > a {
    color: #999 !important;
  }
  .cb-menu-light #cb-nav-bar .cb-main-nav > li > a:hover {
    color: #888 !important;
    border-bottom: solid 3px #ff7b10 !important;
    padding-bottom: 5px !important;
  }
  .cb-menu-light #cb-nav-bar .cb-main-nav {
    border: 0 !important;
  }
  .cb-body-light #cb-content, .cb-body-light .cb-hp-section {
    background-color: #333 !important;
    padding-top: 40px !important;
  }
  .cb-body-light .cb-sidebar {
    background-color: #444 !important;
  }
  
  /* Titles */
  .cb-body-light h1, .cb-body-light h2, .cb-body-light h3, .cb-body-light h4, .cb-body-light h5, h1, h2, h3, h4, h5 {
    color: #888 !important;
  }
  .cb-body-light .cb-module-header .cb-module-title::after {
    background: #ff7b10 !important;
  }
  .cb-body-light .cb-widget-title {
    border-bottom: solid 3px #ff7b10 !important;
  }
  .cb-body-light .cb-module-block .cb-post-title a {
    color: #777 !important;
  }
  .cb-body-light .cb-post-meta .cb-element a {
    color: #999 !important;
    background: #444 !important;
  }
  
  /* Article */
  .mashsb-container {
    display: none !important;
  }
  .cb-breadcrumbs a {
    color: #888 !important;
  }
  
  /* Footer */
  footer {
    display: none !important;
  }

}
