css:
@-moz-document url-prefix("http://it.rfet.ru/my"), url-prefix("http://it.rfet.ru/course/") {
body {
    color:#ffffff;
    background: #404040;
    font-family: Opel Sans;
}
span {
    color:#ffffff;
    font-family: Opel Sans;
}
h2 {
    color:#ffffff;
    font-size: 29px;
    font-family: Opel Sans;
}
div.subtle {
    color:#ffffff;
    font-size: 14px;
    font-family: Opel Sans;
}
header.rootlevel {
    color:#ffffff;
    background: #383838;
    font-family: Roboto;
}
footer.rootlevel {
    color:#ffffff;
    background: #383838;
    font-family: Opel Sans;
}
text.label {
    color:#ffffff;
    font-family: Opel Sans;
}

.dropdown {
    background: #3A3A3A;
    font-family: Roboto;
}

.conversation > section > .wrap, .conversation > section textarea {
    background: #3E3E3E;
    font-family: Opel Sans;
}

h4 {
    color: #FFF;
    font-family: Opel Sans;
}

html {
    background: #262626;
    font-family: Opel Sans;
}
h1 {
    color: #148DBF;
    font-family: Opel Sans;
}
table.rows tr.border-bottom td, table.rows tr.border-bottom th {
    color: #FFF;
    font-family: Opel Sans;
}
.courseCard .cover .wrap h1 {
    margin: 0;
    font-size: 17px;
    line-height: 28px;
    color: #333;
    font-family: Opel Sans;
}

div.small {
    font-size: 11px;
    font-family: Opel Sans;
}

.block.definition {
    background: linear-gradient(to right, #3E3E3E, #3E3E3E);
    font-family: Opel Sans;
}
.subtle, .sidenote{
    color: rgb(199, 135, 135);
    font-family: Opel Sans;
}
}
