css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(https://ekstrabladet.dk), domain(nationen.ebcomments.dk), domain(eb.dk) {

/* coloring */
a,article,blockquote,button,div,body,h1,h2,h3,h4,h5,h6,header,input,label,li,lu,section,span,::placeholder
{background-image: #252525 !important; background-color: #252525 !important;color: #fff !important}
:link,:hover,:visited,:active,:focus{color:#fff;text-decoration:none;outline: none;}

/* improvements */
aside {display: none !important}
.pad-m--t{padding-top: 0;}
body{padding:0px !important}
.drfront .df-article {border-top: 0 solid #e5e5e5;}
.fb-post > span{background-color: #eee !important}
article{box-shadow: 0 0 0 1px #efefef;}
.comments-container{border: 1px solid #fff}

/* ads */
.side-column,.eb-position--monster,.eb-position--artikelbannerstor,.widget-image,.manipulate_apn_monster,#manipulate_apn_mega2,#manipulate_apn_sponsor2,#ebSidecolumn_left,#ebSidecolumn_right,#manipulate_apn_mega1,div.article-wrapper > div > div > div > div.pagecolumn-element:nth-child(2) > div:nth-child(1){display: none !important}

/* icons */
.indicator-video {background: url(https://ekstrabladet.dk/assets/base/svg/play-icon.svg) no-repeat !important;}

/* video player */
.image-container--landscape * {background-color: transparent !important}
.playkit-seek-bar * {background-color: transparent !important}
.playkit-progress-bar {background-color: rgba(255, 255, 255, 0.3) !important}
.playkit-progress {background-color: red !important}

/* kommentarer */
.talk-load-more:hover button,.talk-load-more button,.talk-load-more button:active,.talk-load-more button:hover {border:#fff solid 1px;background-image:-webkit-gradient(linear,left top,left bottom,from(#252525),to(#252525));background-image:-webkit-linear-gradient(top,#252525,#252525);background-image:-moz-linear-gradient(top,#252525,#252525);background-image:-o-linear-gradient(top,#252525,#252525);background-image:-ms-linear-gradient(top,#252525,#252525);}
.talk-stream-comment-wrapper-level-0 {border-top:1px solid #fff}

/* art-gallery */
.link-gallery-item-content {background-color: transparent !important}
.eb-row.eb-row--bottom.height1of1{background-color: transparent !important}

/* facts */
.facts-coverup {display: none !important}
.transition-expand,.article-widget-body {margin:0px;padding-bottom:0px;}
.facts-expand,.cb-expand-hidden {width: 100%;border: 1px solid #fff;padding:5px !important;}

/* scribble */
.scrb-pinned-container {border: 1px solid #bd1118}
.scrb-postcontainer {border-bottom: 0px solid #fff}
.scrb-postcontent * {background-color: transparent !important}
.scrb-meta * {background-color: #252525 !important}
.scrb-btn {width: 100%;height: 50px;border: 1px solid #bd1118;}
.scrb-sendbutton {width: 40%;border: 1px solid #fff;}

/* DC-embed */
.DC-embed * {background-color: transparent !important}

}
