css:
@-moz-document url-prefix("https://www.yikyak.com/") {
body {
	background-color: #192120 !important;
}

.panel.panel-default {
    background-color: #374442;
}

.message-container .message-body .message .message-text {
    color: white;
}

.handle {
    color: #E3E8E7;
}

.message-container.post-message-container .post-message .form-group .form-control {
    color: white;
}

.comment-text {
	color: white;
}

.form-group {
	color: white !important;
    background-color: #374442 !important;
}

.form-control {
	color: white !important;
}

.toggle-container label {
    color: #e0e0e0 !important;
}


.toggle-container .toggle-background {
    background-color: #0DD5B2 !important;
}

.like .vote.downvote.active {
    color: #111312;
}

.navbar .container .nav.navbar-nav .nav-item, .navbar .container .nav.navbar-nav .navbar-btn {
    color: #0DD5B2;
}

.navbar .container .nav.navbar-header {
    background-color: #374442;
}

.refresh-action:hover i {
    color: #0DD5B2;
}

.navbar .container .nav.navbar-nav .nav-item:hover, .navbar .container .nav.navbar-nav .nav-item.active, .navbar .container .nav.navbar-nav .nav-item:active, .navbar .container .nav.navbar-nav .navbar-btn:hover, .navbar .container .nav.navbar-nav .navbar-btn.active, .navbar .container .nav.navbar-nav .navbar-btn:active {
    color: #FFFFFF;
}

.content-overlay {
    background-color: #101514;
}

.content-overlay .close-overlay {
    background-color: #0dd5b2;
}

.content-overlay .close-overlay:hover {
    background-color: #0CCEAC;
}
.post-comment {
    background-color: #374442;
}
.post-comment .form-group .comment-input {
	background-color: #374442;
}
}
