css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.yahoo.co.jp/),
  domain(search.yahoo.co.jp){

   html,
  body{
  background-image: url("https://farm6.staticflickr.com/5772/30153109073_0f76c08b90_h.jpg");
  /*＊＊＊＊＊＊＊＊＊＊＊＊＊＊↑↑ここに画像までのパスを入れると，好きな画像を設定できます。***********************/
  /*********背景画像は，予告なく変更・削除される場合がありますので，ご了承ください。********************/
  background-color: #fff;
  background-repeat: no-repeat;
   background-size: cover;
  }
    
    #wrapper{
      margin: 0 !important;
    }
    
  a{
    color: #000040;
    font-weight: 900;
    text-shadow: 1px 1px 1px #fbb;
  }
  #masthead{
    background-color: rgba(255,255,255,0);
    background-image: none !important;
  }
 /********************ヘッダー部分要らないもの削除***************************/ 
  #uhd, #mhicon, #siteinfo, #siteinfo, #changeMode, .symbol
   {
    display: none;
  }
  
  
/********************右カラム要らないもの削除***************************/ 
  
  #yads-trb-pr, #yjidbox, #scoreboard, #qurioRanking, #ultra, #pageTop, #brandpanel, #rightbox, div[id^="ADframe"], div#rightbox
  {
    display: none !important;
  }
  
/********************左カラム要らないもの削除***************************/ 
  
 /* #navi*/#favoriteservice, #companybox, #disasterbox, #announce
  {
    display: none;
  }
  
  
  
/********************中央カラム要らないもの削除***************************/
   #video, #cgmboxR, #event, #qurioRecommend,
  div#topicsbox ~ div
  {
    display: none;
  }
  
    
/********************スタイリング***************************/
    h1{
      padding: 0 !important;
      background-color: rgba(252, 102,166,0.7);
    }
    #masthead{
      margin:0;
      background-color: rgba(252, 102,166,0.7);
    }
  #srchfield, #personalbox, .hd, #yahooservice, #topicstab > li
  {
        background-color: rgba(252, 102,166,0.7);
  }
    #searchbox{
      border: 0 !important;
      border-width: 0 !important;
      padding: 0 !important;
          background-color: rgba(252, 102,166,0.7);
    }
  #subfooter{
    background-color: rgba(252, 102,166,0.7);
    color: #333;
  }
  

/**************************検索結果*******************************/
  #header
  {
    margin: 0 !important;
    background-color: rgba(252, 102,166,0.7);
    
  }
   
  #yschsp, #yschsbp{
    border-radius: 100px 25px 50px 50px / 50px 25px 50px 25px;
  }
  #Sf, #Sf2{
    border-bottom: 0px !important;
    background-color: rgba(252, 102,166,0);
  }
  #So3, #WS2ak{
    display:none;
  }
    
  #contents, #SaA-dtl, #ygma, #Se, #Sq .inner, #Sf {
    margin: 0 0 !important;
    width: 950px;
}
   #mIn, #footer, #Sq .inner, #SqC {
      background-color: rgba(252, 102,166,0.8);
    }
    #sq, .inner, #SqC{
      margin: 0;
     border:0;
    }
    .inner, .cf{
      border: 0 !important
    }
}    
/**************************画像検索結果*******************************/    
@-moz-document domain("image.search.yahoo.co.jp") {
  body{
    
  background-image: url("https://farm6.staticflickr.com/5778/30752042896_5655749e0b_h.jpg");
  /*＊＊＊＊＊＊＊＊＊＊＊＊＊＊↑↑ここに画像までのパスを入れて下さい***********************/
  background-color: #fff;
  background-repeat:repeat-y;
  background-attachment: fixed;
  background-position: right bottom;
  }
  
   #mIn {
      background-color: rgba(0, 102,176,0.8);
    }
  #Sf, #Sf2{
    border-bottom: 0px !important;
    background-color: rgba(0, 102,176,0);
  }
#IS2r_2, #So1>.bd,  #So2
    {
      display: none!important;
    }
    #topcontrol a{
      border-radius: 100px 25px 50px 50px / 50px 25px 50px 25px;
    }
}
