@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.mmo-champion.com"), domain("blue.mmo-champion.com") {

       /* DESIGN CHANGES */
/* remove space after recent posts */ .bar > .border {margin-right: 0px !important;} 
/* make footer more centered */       .copyright {margin-left: 0px !important;} 
/* make forum page larger */          .site-mmo-champion #page {width: unset !important;}
    
       /* Adblock Notices */
/* remove space after recent posts */ .soft-container {display:none !important;}
       
       /* ADVERTISEMENT */
/* frontpage top ad */                #ad-horizontal-header {display:none !important;}
/* frontpage top ad */                div [style="text-align:center;margin-left:auto;margin-right:auto;width:900px;margin-bottom:10px;"] {display:none !important;}
/* frontpage bottom ad */             #ad_global_above_footer {display:none !important;}
/* frontpage recent ad */             .recent-ad {display:none !important;}
/* frontpage ad */                    #cdm-zone-05 {display:none !important;}
/* frontpage curse bottom ad */       .show-ads {display:none !important;}
/* frontpage legion ad */             #column1 > div:nth-child(4) > a:nth-child(1) > img:nth-child(1) {display:none !important;}
/* bluepage curse bar ad */           #netbar {display:none !important;}
/* bluepage clicky background ad */   #skin_cdm {display: none !important;}
/* bluepage background ad */          body {background: url(http://static.mmo-champion.com/images/tranquilizing/bg.jpg) repeat-x, url(http://static.mmo-champion.com/images/tranquilizing/bg_repeat.jpg)!important;}
/* bluepage recent ad */              .mpu_advertisement {display:none !important;}        
/* bluepage top ad */                 #leaderboard_top {display:none !important;}
/* bluepage bottom ad */              #leaderboard_bottom {display:none !important;}
/* bluepage curse bottom ad */        footer#footer {display:none !important;}
/* forum bottom ad */                 .zergheader,.zerglayoutcl,#ad_forum_below_threadlist {display:none !important;}

       /* OPTIONAL */
/* Top bar */                        .bar{display: none !important;}
       
}

@-moz-document domain("blue.mmo-champion.com") {
    
       /* DESIGN CHANGES */
/* make bluepage larger */            #site {width: 1240px!important;}.bluetracker {width: unset !important;}
/* make bluepage recent larger */     .recentbar {width: 100%}.recent {width: 48.3% !important;margin: 5px 5px 5px 5px !important;}
    
}
