css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("comfy.moe") {
  
  body {
    background-image:url('https://comfy.moe/japveb.png');
    background-size: cover;
    color: #DADADA;
    background: #3A3A3A;
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 20px;
  }

  nav {
    display: none !important;
  }

  .jumbotron .btn {
    background: #787878;
    background-image: url(https://comfy.moe/img/up.png);
    background-size: cover;
    border: 0px solid #D1D1D1;
    color: #ADADAD;
    transition: background-color .25s,width .5s,height .5s;
    cursor: pointer;
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    margin: auto;
    vertical-align: middle;
    height: 200px;
    width: 200px;
  }
  
  .jumbotron .btn:hover, .jumbotron .btn:active, .jumbotron .btn:focus, .jumbotron .btn.drop {
    background-color: #68B459;
    text-decoration: none;
  }
  
  .file-url a {
    color: #DADADA;
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 20px;
  }
  
  .file-url:hover a:hover {
    color: #7DDB6B;
  }
  
}
