css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

popup,menupopup 
  {background: url(http://i1164.photobucket.com/albums/q575/Sanya4ever/gif/33_zpscqqb0ucj.gif);
  background-color: rgba(0, 0, 0,  0.8)!important; color: #3ce!important;background-position: center center !important;background-repeat: no-repeat !important;background-attachment:scroll !important; background-size: contain !important;transition:all .4s ease-in-out !important;}

menu,menuitem,.splitmenu-menuitem {-moz-appearance: none !important;color: #3ce !important;}
menu:hover,menuitem:hover,.splitmenu-menuitem:hover {-moz-appearance: none !important;color: rgb(255,0,0) !important;}

/*  BACKGROUND MENU */
menupopup:not([id="BMB_bookmarksPopup"]),#BMB_bookmarksPopup menupopup[placespopup=true] > hbox{
    -moz-appearance: none !important;
    border-radius: 9px !important;    
  
    box-shadow:0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
    background-color: rgba(0, 0, 0,  0.7)!important;
    background-image:url(http://i1164.photobucket.com/albums/q575/Sanya4ever/gif/33_zpscqqb0ucj.gif) !important;}

menupopup:not([id="BMB_bookmarksPopup"]){
    margin-top: -0px ;
    padding-top: 5px ;
    padding-bottom: 5px ;}
menubar > menu > menupopup {margin-top: 0px !important;}
