css:
@-moz-document domain("www.matematikfessor.dk") {
elementstyle {
}

body {
    margin-top: 3.4em;
    padding: 0 .7em;
    background: #fafafa;
    background: #fafafa linear-gradient(to bottom, #f5f5f5, #ffffff);
    background: -webkit-gradient(linear, left top, left bottom, from(#9B2D2D), to(#D44C4C));
}
elementstyle {
}
body.unfixMenus #pageHeader {
    position: absolute;
}
#pageHeader {
    font-size: 1.1em;
    top: 0;
    left: 0;
    right: 0;
    position: fixed;
    height: 38px;
    background: #2ca6e5;
    background: #2ca6e5 linear-gradient(to bottom, #38abe7, #21a1e4);
    background: -webkit-gradient(linear, left top, left bottom, from(#D44c4c), to(#9b2d2d));
}
elementstyle {
}
#frame {
    min-height: 400px;
    position: relative;
    text-align: left;
    padding: 1em;
    margin-top: -1px;
    background-color: #A03131;
}
elementstyle {
}
.testExecutor > .questionFrame {
    position: relative;
    z-index: 2;
    font-size: 1.2em;
    margin: 0 35px 0 170px;
    min-height: 230px;
    overflow: auto;
    padding: 1em 1em 1.5em;
    background-color: #A03131;
    -webkit-box-shadow: 0 0.1em 0.3em rgba(0, 0, 0, 0.18);
    box-shadow: 0 0.1em 0.3em rgb(190, 64, 64);
    background-position: left bottom;
    border: 1px solid #A03131;
    -webkit-border-radius: 0.3em;
    border-radius: 0.3em;
}
element.style {
}
.homeworkNavigation.finished {
    background: #ebf6e7;
    background: #ebf6e7 linear-gradient(to bottom, #eef7ea, #e8f4e3);
    background: -webkit-gradient(linear, left top, left bottom, from(#9C2D2D), to(#BD3F3F));
}
}
