css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lesnumeriques.com") {
.carousel {
    display: none;
}
  
.card.mod-card-news:nth-child(n) {
    height: 130px;
    width: 100%;
}
.card.mod-card-news img {
    display: inline-block;
    float: left;
    width: 20%;
}
  .card.mod-card-news:nth-child(n) .card-transparent{
    display:none;
  }
  .card .card-content {
     height: auto;
    left: 20%;
    width: 80%;
}
    
.block-news .cards-stack, .block-tests .cards-stack {
    height: auto;
    }
}
