css:
@-moz-document domain("webpack.github.io") {
/*
@import url(https://fonts.googleapis.com/css?family=Merriweather:300,400,300italic,800);
@import url(https://fonts.googleapis.com/css?family=Source+Code+Pro:400,300);
@import url(https://fonts.googleapis.com/css?family=Open+Sans:800,700,300,300italic);
*/

body {
    font-family: 'Merriweather', serif;
    font-weight: 300;
    font-size: 18px;
    line-height: 2;
}
h1, h2, h3, h4, h5, h6 {
    font-family: 'Open Sans', sans-serif;
    font-weight: 700;
    margin-top:2em;
    margin-bottom:1em;
}    
  
h1 {
    font-size: 2.4em;
    font-weight: 300;
}
h2 {
	font-size: 1.4em;
}
h3 {
	font-size: 1.2em;
}
p {
	margin: 1em 0;
}
code {
    font-family: 'Source Code Pro',monospace;
    font-weight: 400;
    background: #F7FAFB;
}      
pre {
    border: #E3EDF3 1px solid;
    background: #F7FAFB;
}

.page-header {
	background:none;
    border:none;
    text-align: left;
}
.page-header h1 {
	font-family: 'Merriweather', serif;
    font-size: 4em;
    color: #A1BFE8;
}

.nav {
	background: #F7FAFB;
}    
}
