@-moz-document domain("www.eldarya.fr") {
/* header bleu foncé */
#header #header-top {
	background-color: #34386F;
}

/*player */
#player-headerPortrait {
	border: none;
    background-image : url("http://img15.hostingpics.net/pics/119185playerportraitbackground2.png") ;
     width: 104px;
    height: 104px;
}

#player-headerPortrait a {
    background-color: #bec9f8;
    width: 93px;
    height: 92px;
    margin : 6px 6px;
    
}

#pet-headerPortrait {
	border: none;
    background-image : url("http://img15.hostingpics.net/pics/119185playerportraitbackground2.png") ;
    background-size: 84px 84px;
    width: 84px;
    height: 84px;
}

#pet-headerPortrait #pet-headerPortrait-base {
