    width: 75px;
    height: 74px;
    margin : 5px 5px;
}

#player-info-level {
    background-image : url("http://img15.hostingpics.net/pics/114688playerportraitbackground.png") ;
    background-size: 33px 33px;
    width: 33px;
    height: 33px;
    color: #fff;
    background-color: #00e4ff;
}

/* point aquis */
#player-info-dots .player-info-dot.lit {
   background-image: radial-gradient(ellipse ,#675e71, #36386b); 
}
