css:
@-moz-document domain("lichess.org") {
/* analysis page ranks fix*/
body.coords_2 .gauge_displayed .cg-board-wrap coords.ranks {
    right: -18px !important;
}
.cg-board-wrap coords.ranks coord {
    transform: translateY(39%) !important;
}
.orientation-white ~ coords.ranks {
    left: -18px !important;
}

body.light .cg-board-wrap coords {
    color: #454545 !important;
    text-shadow: none !important;
}

/* accomodate ranks on the right when gauge present */
div.gauge_displayed div.lichess_game div.lichess_ground {
    padding-left: 45px;
}
div.eval_gauge.reverse {
    right: -30px;
}
div.eval_gauge {
    right: -23px;
}

.cg-board-wrap coords {
    font-family: 'Courier New', monospace;
    font-size: 20px;
    font-weight: bold;
    color: #ffffff;
}

.cg-board-wrap coords.ranks {
    right: -18px;
}

.cg-board-wrap coords.files {
    bottom: -20px !important;
    text-transform: lowercase;
    text-align: center !important;
}

div.lichess_game div.lichess_board_wrap {
    position: relative;
    padding-right: 12px;
    padding-left: 12px;
}

div.analysis_panels {
    margin-top: 0;
}

#board_editor .spare.bottom {
    margin-top: 30px;
}

div.underboard {
    margin-top: 30px;
}

.lichess_ground .variation move {
	font-family: 'Courier New', monospace;
}

.lichess_ground .replay turn>move {
	font-family: 'Courier New', monospace;
}

div.game_row .pgn {
	font-family: monospace;
    font-size: 13px;
    padding-top: 4px;
}

.explorer_box .moves td:first-child {
	font-family: 'Courier New', monospace;
}

.tview2 move {
	font-family: 'Courier New';
}
}
