css:
@-moz-document domain("wccftech.com") {
header {
    filter: invert();
}
body {
    background-color: #101010;
    color: #777777;
}
.above-the-fold {
    background: #101010;
}
.content {
    background: #171717;
    padding: 30px;
}
.post:hover {
    background: #252525;
}
.topics a:hover {
    color: #b00000;
}
a {
    color: #cccccc;
}
.trending-posts li {
    border-bottom: 1px solid #252525;
}
.shares:hover,
.sohail-share {
    background: #1f2529;
}
.sticky-hardware h2,
.sticky-gaming h2,
.sticky-mobile h2 {
    text-align: center;
}
.sticky h2 span {
    border-radius: 4px;
    font-weight: 500;
    background: #101010;
    padding: 2px 10px 0px 10px;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-hardware > h2,
body > aside.sticky.wrap > section.sticky-hardware > h2,
body > main > aside:nth-child(2) > div > section.sticky-hardware > h2 {
    border-color: #ff543a;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-gaming > h2,
body > aside.sticky.wrap > section.sticky-gaming > h2,
body > main > aside:nth-child(2) > div > section.sticky-gaming > h2 {
    border-color: #45d87c;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-mobile > h2,
body > aside.sticky.wrap > section.sticky-mobile > h2,
body > main > aside:nth-child(2) > div > section.sticky-mobile > h2 {
    border-color: #0fa6f3;
}
.sidebar section h3 span {
    background: #464646;
    color: #ffffff;
}
.sidebar section h3 {
    border-bottom: 2px solid #464646;
}
.exclusives h2 span {
    background: #ffffff;
    color: #000000;
    font-size: 1.325em;
}
.exclusives h2 {
    border-color: #ffffff;
}
.highlights {
    background: #101010;
}
.highlights h2 span {
    background: #101010;
    color: #cccccc;
    position: absolute;
}
.highlights h2 {
    border-color: #464646;
}
.body p,
.body ul,
.body ol,
.body table {
    color: #aaa;
}
.meta a {
    color: #eb0254;
}
.meta a:hover {
    color: #ffffff;
}
.meta-author,
.meta-left {
    border-color: #404040;
}
.meta-share-fb,
.meta-share-tw,
.meta-share-rd {
    visibility: hidden;
}
.meta-share {
    width: 70px;
    float: right;
    height: 52px;
    padding-top: 7px;
    text-align: right;
}
.meta-left {
    width: calc(100% - 75px);
}
.body h1,
.body h2,
.body h3,
.body h4 {
    color: #6d6d6d;
}
.wp-caption-text {
    color: #a0a0a0 !important;
    background: #222222;
}
.wp-gallery a:first-child {
    max-width: 840px;
}
.wp-gallery.clearfix {
    min-width: 840px;
    margin-left: -30px;
}
.wp-caption.clearfix {
    max-width: 840px;
}
.wp-gallery.clearfix > a {
    border-color: #353535;
}
table {
    color: #cccccc!important;
}
.tablepress .even td {
    background-color: #151515;
}
.tablepress .odd td {
    background-color: #202020;
}
.tablepress tbody td,
.tablepress tfoot th,
.tablepress tfoot th:last-child,
.tablepress thead th:last-child,
.tablepress tbody tr td:last-child {
    border-color: #353535;
}
.tablepress tfoot th,
.tablepress thead th {
    background-color: #272727;
}
.tablepress .row-hover tr:hover td {
    background-color: #464646;
}
.thumbnails-a .trc_rbox_header,
.thumbnails-a .syndicatedItem .video-title {
    color: #999999;
}
.lg-backdrop {
    background-color: #101010;
}
.lg-toolbar {
    background-color: #222222;
}
.lg-sub-html {
    background-color: #222222;
    color: #999999;
}
#lg-counter > div {
    filter: invert();
}
body > aside.sticky.wrap > div > section.sticky-hardware > h2 {
    border-color: #ff543a;
}
body > aside.sticky.wrap > div > section.sticky-gaming > h2 {
    border-color: #45d87c;
}
body > aside.sticky.wrap > div > section.sticky-mobile > h2 {
    border-color: #0fa6f3;
}
.sticky li.first a {
    min-width: 340px;
}
.section-head span {
    background: #464646;
    color: #ffffff;
}
.section-head {
    border-color: #464646;
}
.review-verdict {
    border-color: #fed432;
    background: #101010;
}
.review-verdict h3 {
    color: #45d87c!important;
    border-bottom-color: #444444;
}
.review-cons h3 {
    color: #EB0254!important;
}
.logo {
    filter: saturate(2) contrast(0.9) hue-rotate(330deg);
}
blockquote {
    border-color: #464646;
}
#jp-relatedposts h3.jp-relatedposts-headline {
    margin: 0px 270px 0.5em!important;
    font-size: 2em!important;
}
.size-large,
.size-full {
    min-width: 100%;
}
main {
    background-color: #101010;
    padding: 0px 50px;
}
.featured-posts {
    background-color: #222222;
}
.post,
.youtube-channel {
    border-color: #222222;
}
.post .text {
    padding-top: 13px;
    padding-left: 250px;
    padding-right: 10px;
}
.lSSlideOuter {
    background-color: #252525;
}
.review-info h3 {
    border-color: #333333;
    background: #333333;
}
.review-info,
.review-info div,
.review-info .right {
    border-color: #333333;
}
.feedback-button {
    background: #171717;
}
.popular-posts li a {
    color: #eeeeee;
}
p.wp-caption-text {
    width: 680px;
}
.wccf-gallery {
    background: #252525;
}
blockquote {
    background: #232323;
    color: #5c5c5c;
}
.single .sidebar section {
    margin-bottom: 50px;
}
body > main > section > div.body.clearfix > div > a {
    color: #aaaaaa!important;
}
.wccf_related_post a:hover,
.wccf_related_post a:active {
    background: #232323;
}
.wccf-stock-table tr:nth-child(odd) {
    background-color: #202020;
}
.wccf_related_post a {
    color: #acacac !important;
}
}
