/* STYLE BY UNHOLYMASTER || TUMBLR & INSTAGRAM */

@-moz-document domain("userstyles.org") {

#content-wrapper, html {
background: #000000;
}

a {
color: #9043cc;
}

a:hover {
color: #be7fef;
}

#main-header>h1, .h1, h1 {
font-family: 'Parisienne';
}

#left-sidebar li {
color: #444444;
box-shadow: -1px -1px 0px rgba(255,255,255,0.3), 1px 1px 0px rgba(0,0,0,0.8);
}

#main-header {
color: #444444;
background: #0a0a0a;
padding: 5px;
margin-bottom: 10px;
margin-left: 0px;
border-radius: 5px;
box-shadow: -1px -1px 0px rgba(255,255,255,0.3), 1px 1px 0px rgba(0,0,0,0.8);
}

#main-header:hover {
box-shadow: 0px 0px 20px #000000;
-moz-transition: all 1s ease-out;
-o-transition: all 1s ease-out;
-webkit-transition: all 1s ease-out;
transition: all 1s ease-out;
}

#body, #left-sidebar>*, .body {
font-family: 'Raleway', serif;
background: #0a0a0a;
color: #444444;
}

#left-sidebar {
background: #0a0a0a;
-moz-transition: all 1s ease-out;
-o-transition: all 1s ease-out;
-webkit-transition: all 1s ease-out;
transition: all 1s ease-out;
box-shadow: -1px -1px 0px rgba(255,255,255,0.3), 1px 1px 0px rgba(0,0,0,0.8);
}

#left-sidebar:hover {
box-shadow: 0px 0px 20px #000000;
-moz-transition: all 1s ease-out;
-o-transition: all 1s ease-out;
-webkit-transition: all 1s ease-out;
transition: all 1s ease-out;
box-shadow: -1px -1px 0px rgba(255,255,255,0.3), 1px 1px 0px rgba(0,0,0,0.8);
}

#content-wrapper {
font-family: 'Raleway', serif;
color: #444444;
}

.install-info {
background: #9043cc;
background-image: -moz-linear-gradient(#9043cc,#be7fef);
background-image: -webkit-linear-gradient(#9043cc,#be7fef);
background-image: -o-linear-gradient(#9043cc,#be7fef);
background-image: linear-gradient(#9043cc,#be7fef);
box-shadow: -1px -1px 0px rgba(255,255,255,0.3), 1px 1px 0px rgba(0,0,0,0.8);
}

.install-info:hover {
background: #9043cc;
background-image: -moz-linear-gradient(#9043cc,#be7fef);
background-image: -webkit-linear-gradient(#be7fef,#9043cc);
background-image: -o-linear-gradient(#9043cc,#be7fef);
background-image: linear-gradient(#be7fef,#9043cc);
box-shadow: -1px -1px 0px rgba(255,255,255,0.3), 1px 1px 0px rgba(0,0,0,0.8);
}

.install {
background: #9043cc;
background-image: -moz-linear-gradient(#9043cc,#be7fef);
background-image: -webkit-linear-gradient(#be7fef,#9043cc);
background-image: -o-linear-gradient(#9043cc,#be7fef);
background-image: linear-gradient(#be7fef,#9043cc);
box-shadow: -1px -1px 0px rgba(255,255,255,0.3), 1px 1px 0px rgba(0,0,0,0.8);
}

.install:hover {
background: #9043cc;
background-image: -moz-linear-gradient(#be7fef,#9043cc);
background-image: -webkit-linear-gradient(#9043cc,#be7fef);
background-image: -o-linear-gradient(#be7fef,#9043cc);
background-image: linear-gradient(#9043cc,#be7fef);
box-shadow: -1px -1px 0px rgba(255,255,255,0.3), 1px 1px 0px rgba(0,0,0,0.8);
}

.more {
border-top: 0px;
}

/*@import url(http://fonts.googleapis.com/css?family=Raleway);*/
@font-face {
font-family: 'Raleway';
font-style: normal;
font-weight: 400;
src: local('Raleway'), url(http://fonts.gstatic.com/s/raleway/v9/IczWvq5y_Cwwv_rBjOtT0w.woff) format('woff');
}

/*@import url(http://fonts.googleapis.com/css?family=Parisienne);*/
@font-face {
font-family: 'Parisienne';
font-style: normal;
font-weight: 400;
src: local('Parisienne'), local('Parisienne-Regular'), url(http://fonts.gstatic.com/s/parisienne/v4/1wiHF6pWOa568Cj3p-anxPk_vArhqVIZ0nv9q090hN8.woff2) format('woff2');
}

}
