@-moz-document domain("spellsofmagic.com") {

#l, .body, .cal, .cols-49, .fi, .l, .public_chat_internal_div, body {
background: #212121;
}

.element.style, .w, element.style {
background: #000000;
color: #4f4f4f;
}

.fp, tl {
background: #191919 !important;
}

.tr {
background: #191919;
border-top: #191919;
}

.mm, .mm2, .sm, .sm2 {
color: #4f4f4f;
background: #212121;
border-top: 2px solid #191919;
border-left: 2px solid #191919;
border-right: 2px solid #191919;
}

.h, .hr, .hrl, .hrr {
