}

.mobile, a {
color: #d8d8d8;
}

body, center, html, input, td {
background: #000000;
}

.cal2, .cal3 {
background: #d9d9d9;
}

.lbar {
display: none !important;
}

.fcx, .fcx2 {
border-left: 1px solid #212121;
}

input {
background: #212121;
color: #d9d9d9;
}

.header_bar2 {
border: solid 1px #191919;
color: #4f4f4f;
background: #212121;
padding: 2px;
}

.mm:hover {
background: #d9d9d9;
color: #4f4f4f;
border-top: 2px solid #4f4f4f;
border-left: 2px solid #4f4f4f;
border-right: 2px solid #4f4f4f;
}

.sm:hover, .sm2:hover {
color: #d9d9d9;
background: #4f4f4f;
border-top: 2px solid #d9d9d9;
border-left: 2px solid #d9d9d9;
border-right: 2px solid #d9d9d9;
}

.mm2:hover {
color: #4f4f4f;
background: #d9d9d9;
border-top: 2px solid #4f4f4f;
border-left: 2px solid #4f4f4f;
border-right: 2px solid #4f4f4f;
}

.dash {
border-bottom: 1px dashed #212121;
border-right: 1px dashed #212121;
}

.ft, .fi:hover {
color: #d9d9d9;
}

.mp {
background-image: url('http://i58.tinypic.com/2lt4ymp.jpg') !important;
z-index: 100000000;
width: 50px;
}

element.style {
border: 1px solid #212121;
}

}
