css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dynalist.io") {
  

#blank-slate div ul li a {text-decoration: none;}
  
    
a {
	color:#66F;
        font-weight: normal;
	text-decoration:none;
}

.Node.is-currentRoot > .Node-self {font-weight: bold;}

.Node-bullet:before {
	color:#66f;
	top: 10px;
}

.Node-children {border-left: 0px;font-weight:normal;}


.is-comfortableDensity .Node-content,
.is-comfortableDensity .Node-renderedContent {
  padding: 10px 0;
}

#inline-image-preview {
  max-width: 800px;
  max-height: 600px;
}
}
