css:
@-moz-document domain("blitztactics.com") {
::-webkit-scrollbar {
	visibility: hidden;
}

body {
	background-color: rgba(0, 0, 0, 1.0);
}

body > header {
	display: none
}

body > div.above-board {
	height: 14px
}

body > div.above-board > div.puzzle-hint {
	display: none
}

.chessboard .square.highlighted.move-from, .chessboard .square.highlighted.move-to {
	background-color: rgb(255, 247, 155);
}

.above-board .move-status {
	padding: 6px 0px 0px 624px;
    white-space: nowrap
}

.sidebar .times {
 	padding-top: 50px   
}

.under-board .level-name {
    font-family: 'Liberation Sans';
    font-size: larger;
    font-weight: bold;
}

.under-board .next-stage {
    height: auto
}
}
