css:
@-moz-document domain("therightstuff.biz") {
div.content blockquote, div.preview blockquote {
    color: #4DCE4D;
}

div.content blockquote p::before, div.preview blockquote p::before {
    content: "> ";
    white-space: pre;
}

div.content blockquote br::after, div.preview blockquote br::after {
    content: "> ";
}

div.content blockquote p, div.preview blockquote p {
    margin: 0 0 0.5em;
    text-indent: -0.75em;
}

#grom-chrome::-webkit-slider-thumb, div.content blockquote br, div.preview blockquote br {
    content: "A" !important;
    display: block !important;
    margin: 0 0 -1.5em 0em !important;
}

#grom-firefox::-moz-selection, div.content blockquote br, div.preview blockquote br {
    content: "A" !important;
    display: block !important;
    margin: 0 0 0em 0em !important;
}
}
